import { Content, GenerativeContentBlob, Part } from '@google/generative-ai';
import { EventEmitter } from 'eventemitter3';
import { difference } from 'lodash';
import {
    ClientContentMessage,
    isInterrupted,
    isModelTurn,
    isServerContentMessage,
    isSetupCompleteMessage,
    isToolCallCancellationMessage,
    isToolCallMessage,
    isTurnComplete,
    LiveIncomingMessage,
    ModelTurn,
    RealtimeInputMessage,
    ServerContent,
    SetupMessage,
    StreamingLog,
    ToolCall,
    ToolCallCancellation,
    ToolResponseMessage,
    type LiveConfig,
} from '../types/multimodal-live-types';
import { blobToJSON, base64ToArrayBuffer } from './utils';

/**
 * the events that this client will emit
 */
interface MultimodalLiveClientEventTypes {
    open: () => void;
    log: (log: StreamingLog) => void;
    close: (event: CloseEvent) => void;
    audio: (data: ArrayBuffer) => void;
    content: (data: ServerContent) => void;
    interrupted: () => void;
    setupcomplete: () => void;
    turncomplete: () => void;
    toolcall: (toolCall: ToolCall) => void;
    toolcallcancellation: (toolcallCancellation: ToolCallCancellation) => void;
}

export type MultimodalLiveAPIClientConnection = {
    url?: string;
    apiKey: string;
};

/**
 * A event-emitting class that manages the connection to the websocket and emits
 * events to the rest of the application.
 */
export class MultimodalLiveClient extends EventEmitter<MultimodalLiveClientEventTypes> {
    public ws: WebSocket | null = null;
    protected config: LiveConfig | null = null;
    public url: string = '';

    public getConfig() {
        return { ...this.config };
    }

    constructor({ url, apiKey }: MultimodalLiveAPIClientConnection) {
        super();
        url =
            url ||
            `wss://generativelanguage.googleapis.com/ws/google.ai.generativelanguage.v1alpha.GenerativeService.BidiGenerateContent`;
        url += `?key=${apiKey}`;
        this.url = url;
        this.send = this.send.bind(this);
    }

    log(type: string, message: StreamingLog['message']) {
        const log: StreamingLog = {
            date: new Date(),
            type,
            message,
        };
        this.emit('log', log);
    }

    connect(config: LiveConfig): Promise<boolean> {
        console.log('ðŸŒ [MultimodalLiveClient] Starting connection process...');
        console.log('ðŸ“‹ [MultimodalLiveClient] Config:', config);
        console.log('ðŸ”— [MultimodalLiveClient] URL:', this.url);

        this.config = config;

        console.log('ðŸš€ [MultimodalLiveClient] Creating WebSocket...');
        const ws = new WebSocket(this.url);

        console.log(
            'ðŸ“¡ [MultimodalLiveClient] WebSocket created, readyState:',
            ws.readyState
        );

        ws.addEventListener('message', async (evt: MessageEvent) => {
            if (evt.data instanceof Blob) {
                this.receive(evt.data);
            } else {
                console.log('ðŸ“¨ [MultimodalLiveClient] Non-blob message:', evt);
            }
        });

        return new Promise((resolve, reject) => {
            console.log(
                'â³ [MultimodalLiveClient] Setting up promise handlers...'
            );

            const onError = (ev: Event) => {
                console.error(
                    'âŒ [MultimodalLiveClient] WebSocket error event:',
                    ev
                );
                console.error(
                    'ðŸ” [MultimodalLiveClient] WebSocket readyState on error:',
                    ws.readyState
                );
                this.disconnect(ws);
                const message = `Could not connect to "${this.url}"`;
                this.log(`server.${ev.type}`, message);
                reject(new Error(message));
            };

            ws.addEventListener('error', onError);

            ws.addEventListener('open', (ev: Event) => {
                console.log(
                    'âœ… [MultimodalLiveClient] WebSocket opened successfully!'
                );
                console.log(
                    'ðŸ” [MultimodalLiveClient] WebSocket readyState on open:',
                    ws.readyState
                );

                if (!this.config) {
                    console.error(
                        'âŒ [MultimodalLiveClient] No config available in open handler!'
                    );
                    reject('Invalid config sent to `connect(config)`');
                    return;
                }

                this.log(`client.${ev.type}`, `connected to socket`);
                this.emit('open');

                this.ws = ws;

                console.log(
                    'ðŸ“¤ [MultimodalLiveClient] Sending setup message...'
                );
                const setupMessage: SetupMessage = {
                    setup: this.config,
                };
                this._sendDirect(setupMessage);
                this.log('client.send', 'setup');
                console.log(
                    'âœ… [MultimodalLiveClient] Setup message sent successfully!'
                );

                ws.removeEventListener('error', onError);
                ws.addEventListener('close', (ev: CloseEvent) => {
                    console.log(
                        'ðŸ”Œ [MultimodalLiveClient] WebSocket close event, code:',
                        ev.code,
                        'reason:',
                        ev
                    );
                    this.disconnect(ws);
                    let reason = ev.reason || '';
                    if (reason.toLowerCase().includes('error')) {
                        const prelude = 'ERROR]';
                        const preludeIndex = reason.indexOf(prelude);
                        if (preludeIndex > 0) {
                            reason = reason.slice(
                                preludeIndex + prelude.length + 1,
                                Infinity
                            );
                        }
                    }
                    this.log(
                        `server.${ev.type}`,
                        `disconnected ${reason ? `with reason: ${reason}` : ``}`
                    );
                    this.emit('close', ev);
                });

                console.log(
                    'ðŸŽ‰ [MultimodalLiveClient] Connection process complete, resolving...'
                );
                resolve(true);
            });
        });
    }

    disconnect(ws?: WebSocket) {
        if ((!ws || this.ws === ws) && this.ws) {
            this.ws.close();
            this.ws = null;
            this.log('client.close', `Disconnected`);
            return true;
        }
        return false;
    }

    protected async receive(blob: Blob) {
        const response: LiveIncomingMessage = (await blobToJSON(
            blob
        )) as LiveIncomingMessage;

        if (isToolCallMessage(response)) {
            console.log('ðŸ”§ TOOL CALL RECEIVED by MultimodalLiveClient');
            console.log('ðŸ“ž Tool call response:', response);
            console.log('ðŸ“‹ Function calls count:', response.toolCall?.functionCalls?.length || 0);

            if (response.toolCall?.functionCalls) {
                response.toolCall.functionCalls.forEach((fc, index) => {
                    console.log(`ðŸŽ¯ Function Call ${index + 1}:`);
                    console.log(`  ðŸ“ Name: ${fc.name}`);
                    console.log(`  ðŸ†” ID: ${fc.id}`);
                    console.log(`  ðŸ“‹ Args:`, fc.args);
                });
            }

            this.log('server.toolCall', response);
            console.log('ðŸ“¡ Emitting toolcall event to listeners...');
            this.emit('toolcall', response.toolCall);
            console.log('âœ… Tool call event emitted successfully');
            return;
        }

        if (isToolCallCancellationMessage(response)) {
            this.log('receive.toolCallCancellation', response);
            this.emit('toolcallcancellation', response.toolCallCancellation);
            return;
        }

        if (isSetupCompleteMessage(response)) {
            this.log('server.send', 'setupComplete');
            this.emit('setupcomplete');
            return;
        }

        if (isServerContentMessage(response)) {
            const { serverContent } = response;
            if (isInterrupted(serverContent)) {
                this.log('receive.serverContent', 'interrupted');
                this.emit('interrupted');
                return;
            }
            if (isTurnComplete(serverContent)) {
                this.log('server.send', 'turnComplete');
                this.emit('turncomplete');
            }

            if (isModelTurn(serverContent)) {
                let parts: Part[] = serverContent.modelTurn.parts;

                const audioParts = parts.filter(
                    (p) =>
                        p.inlineData &&
                        p.inlineData.mimeType.startsWith('audio/pcm')
                );
                const base64s = audioParts.map((p) => p.inlineData?.data);

                const otherParts = difference(parts, audioParts);

                base64s.forEach((b64) => {
                    if (b64) {
                        const data = base64ToArrayBuffer(b64);
                        this.emit('audio', data);
                    }
                });

                if (!otherParts.length) {
                    return;
                }

                parts = otherParts;
                const content: ModelTurn = { modelTurn: { parts } };
                this.emit('content', content);
                this.log(`server.content`, response);
            }
        } else {
            console.log('received unmatched message', response);
        }
    }

    sendRealtimeInput(chunks: GenerativeContentBlob[]) {
        let hasAudio = false;
        let hasVideo = false;
        for (let i = 0; i < chunks.length; i++) {
            const ch = chunks[i];
            if (ch.mimeType.includes('audio')) {
                hasAudio = true;
            }
            if (ch.mimeType.includes('image')) {
                hasVideo = true;
            }
            if (hasAudio && hasVideo) {
                break;
            }
        }
        const message =
            hasAudio && hasVideo
                ? 'audio + video'
                : hasAudio
                ? 'audio'
                : hasVideo
                ? 'video'
                : 'unknown';

        const data: RealtimeInputMessage = {
            realtimeInput: {
                mediaChunks: chunks,
            },
        };
        this._sendDirect(data);
        this.log(`client.realtimeInput`, message);
    }

    sendToolResponse(toolResponse: ToolResponseMessage['toolResponse']) {
        const message: ToolResponseMessage = {
            toolResponse,
        };

        this._sendDirect(message);
        this.log(`client.toolResponse`, message);
    }

    send(parts: Part | Part[], turnComplete: boolean = true) {
        parts = Array.isArray(parts) ? parts : [parts];
        const content: Content = {
            role: 'user',
            parts,
        };

        const clientContentRequest: ClientContentMessage = {
            clientContent: {
                turns: [content],
                turnComplete,
            },
        };

        this._sendDirect(clientContentRequest);
        this.log(`client.send`, clientContentRequest);
    }

    updateConfig(newConfig: LiveConfig) {
        if (!this.ws || this.ws.readyState !== WebSocket.OPEN) {
            throw new Error('WebSocket is not connected or not ready');
        }

        const updateEvent = {
            type: "session.update",
            session: {
                ...newConfig,
            },
        };

        console.log("Sending session update event:", updateEvent);
        this.ws.send(JSON.stringify(updateEvent));
        this.config = newConfig;
        this.log('client.updateConfig', 'Session configuration updated');
    }

    _sendDirect(request: object) {
        if (!this.ws) {
            throw new Error('WebSocket is not connected');
        }
        const str = JSON.stringify(request);
        this.ws.send(str);
    }
}
