import React, { useState, useEffect } from 'react';
import { Mic, MicOff, PhoneOff } from 'lucide-react';
import { useLiveAPIContext } from '../contexts/LiveAPIContext';
import { AudioRecorder } from '../lib/audio-recorder';
import AudioVisualization from '../components/AudioVisualization';

const VoiceInterface: React.FC = () => {
    const { client, connected, connect, disconnect, volume, setConfig, sendText } = useLiveAPIContext();
    const [isMuted, setIsMuted] = useState(false);
    const [isSpeaking, setIsSpeaking] = useState(false);
    const [lastMessage, setLastMessage] = useState<string>('');
    const [error, setError] = useState<string>('');
    const [inVolume, setInVolume] = useState(0);
    const [audioRecorder] = useState(() => new AudioRecorder());

    // Set config and connect
    useEffect(() => {
        console.log('🔧 Setting up Gemini Live configuration...');

        setConfig({
            model: "models/gemini-live-2.5-flash-preview",
            generationConfig: {
                responseModalities: "audio",
                speechConfig: {
                    voiceConfig: { prebuiltVoiceConfig: { voiceName: "Aoede" } },
                },
            },
            systemInstruction: {
                parts: [
                    {
                        text: `You are a helpful AI assistant with voice capabilities. You can have natural conversations with users through voice. Keep your responses conversational and engaging since users will hear your actual voice. You are always listening and ready to help - users don't need to click buttons or say wake words, just speak naturally.`,
                    },
                ],
            },
        });
    }, [setConfig]);

    useEffect(() => {
        if (!connected && connect) {
            console.log('🔗 Attempting initial connection...');
            connect()
                .then(() => {
                    console.log('✅ Connection successful!');

                    // Send initial greeting message
                    setTimeout(() => {
                        const initialGreeting = "Hello! I'm your AI assistant. How can I help you today?";
                        console.log('Sending initial greeting...');
                        sendText(initialGreeting);
                    }, 500);
                })
                .catch((err: Error) => {
                    console.error('❌ Connection failed:', err.message || err);
                    setError('Failed to connect to voice service');
                });
        }
    }, [connected, connect, sendText]);

    // Handle microphone audio streaming
    useEffect(() => {
        const onData = (base64: string) => {
            if (client) {
                client.sendRealtimeInput([
                    {
                        mimeType: "audio/webm;codecs=opus",
                        data: base64,
                    },
                ]);
            }
        };

        const onVolume = (vol: number) => {
            setInVolume(vol);
        };

        if (connected && !isMuted && audioRecorder) {
            audioRecorder.on("data", onData).on("volume", onVolume);
            audioRecorder.start().catch((err) => {
                console.error('Failed to start audio recording:', err);
                setError('Failed to access microphone');
            });
        } else {
            audioRecorder.stop();
        }

        return () => {
            audioRecorder.off("data", onData).off("volume", onVolume);
        };
    }, [connected, client, isMuted, audioRecorder]);

    useEffect(() => {
        if (!client) return;

        // Listen for AI responses
        const handleContent = (content: any) => {
            console.log('💬 Received content from AI:', content);
            if (content.modelTurn?.parts) {
                for (const part of content.modelTurn.parts) {
                    if (part.text) {
                        setLastMessage(part.text);
                    }
                }
            }
        };

        const handleAudio = () => {
            setIsSpeaking(true);
        };

        const handleTurnComplete = () => {
            setIsSpeaking(false);
        };

        client.on('content', handleContent);
        client.on('audio', handleAudio);
        client.on('turncomplete', handleTurnComplete);

        return () => {
            client.off('content', handleContent);
            client.off('audio', handleAudio);
            client.off('turncomplete', handleTurnComplete);
        };
    }, [client]);

    const toggleMute = () => {
        setIsMuted(!isMuted);
        console.log(isMuted ? 'Unmuted' : 'Muted');
    };

    const handleDisconnect = () => {
        disconnect();
        console.log('Disconnected from voice service');
    };

    return (
        <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 flex items-center justify-center p-6">
            <div className="text-center">
                {/* Audio Visualization */}
                <AudioVisualization
                    isListening={connected && !isMuted}
                    isSpeaking={isSpeaking}
                    audioLevel={volume || 0}
                    size={400}
                    className="mb-8 transition-all duration-500"
                />

                {/* Connection Status */}
                <div className="mb-6">
                    {connected ? (
                        <div className="flex items-center justify-center space-x-2 text-green-400">
                            <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse" />
                            <span className="text-sm font-medium">Connected & Ready</span>
                        </div>
                    ) : (
                        <div className="flex items-center justify-center space-x-2 text-yellow-400">
                            <div className="w-2 h-2 bg-yellow-400 rounded-full animate-pulse" />
                            <span className="text-sm font-medium">Connecting...</span>
                        </div>
                    )}
                </div>

                {/* Controls */}
                <div className="mb-6 flex items-center justify-center space-x-4">
                    <button
                        onClick={toggleMute}
                        className={`px-8 py-4 text-lg font-semibold rounded-xl transition-all duration-200 flex items-center space-x-2 ${
                            isMuted
                                ? 'bg-red-600 hover:bg-red-700 text-white'
                                : 'bg-green-600 hover:bg-green-700 text-white'
                        }`}
                    >
                        {isMuted ? <MicOff className="w-6 h-6" /> : <Mic className="w-6 h-6" />}
                        <span>{isMuted ? 'Unmute' : 'Mute'}</span>
                    </button>

                    <button
                        onClick={handleDisconnect}
                        className="px-8 py-4 text-lg font-semibold rounded-xl transition-all duration-200 bg-red-600 hover:bg-red-700 text-white flex items-center space-x-2"
                    >
                        <PhoneOff className="w-6 h-6" />
                        <span>Disconnect</span>
                    </button>
                </div>

                {/* Last Message */}
                {lastMessage && (
                    <div className="mt-6 max-w-md mx-auto">
                        <div className="px-4 py-3 bg-white/10 rounded-lg border border-white/20 backdrop-blur-sm">
                            <p className="text-white/80 text-sm font-medium">Last Message:</p>
                            <p className="text-white text-sm mt-1">{lastMessage}</p>
                        </div>
                    </div>
                )}

                {/* Input Volume Indicator */}
                {connected && !isMuted && (
                    <div className="mt-4 max-w-xs mx-auto">
                        <div className="flex items-center space-x-2 text-white/60 text-sm">
                            <Mic className="w-4 h-4" />
                            <div className="flex-1 bg-white/20 rounded-full h-2">
                                <div 
                                    className="bg-blue-400 h-2 rounded-full transition-all duration-100"
                                    style={{ width: `${Math.min(inVolume * 100, 100)}%` }}
                                />
                            </div>
                        </div>
                    </div>
                )}

                {/* Error Display */}
                {error && (
                    <div className="mt-6 max-w-md mx-auto">
                        <div className="px-4 py-3 bg-red-500/20 rounded-lg border border-red-500/40 backdrop-blur-sm">
                            <p className="text-red-100 text-sm">{error}</p>
                            <button 
                                onClick={() => setError('')}
                                className="mt-2 text-red-200 hover:text-white text-xs underline"
                            >
                                Dismiss
                            </button>
                        </div>
                    </div>
                )}

                {/* Instructions */}
                <div className="mt-8 max-w-lg mx-auto">
                    <div className="px-6 py-4 bg-white/5 rounded-lg border border-white/10 backdrop-blur-sm">
                        <h3 className="text-white font-medium mb-2">How to use:</h3>
                        <ul className="text-white/70 text-sm space-y-1 text-left">
                            <li>• Just start speaking - no wake words needed</li>
                            <li>• The AI will respond with voice</li>
                            <li>• Use the mute button to pause listening</li>
                            <li>• The animation shows listening (blue) and speaking (teal) states</li>
                        </ul>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default VoiceInterface;
